import React from 'react';
import { Route, Routes, Navigate, useNavigate } from 'react-router-dom';

import { api } from '../utils/api';
import Header from './Header';
import Login from './Login';
import Register from './Register';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';
import ImagePopup from './ImagePopup';
import EditProfilePopup from './EditProfilePopup';
import EditAvatarPopup from './EditAvatarPopup';
import AddPlacePopup from './AddPlacePopup';
import { CurrentUserContext } from '../contexts/CurrentUserContext';
import InfoTooltip from './InfoTooltip';
import ProtectedRoute from './ProtectedRoute';

import * as auth from '../utils/auth';

function App() {
  const [cards, setCards] = React.useState([]);
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] =
    React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] =
    React.useState(false);
  const [isTooltipPopupOpen, setIsTooltipPopupOpen] = React.useState(false);
  const [isSuccess, setIsSuccess] = React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState({});
  const [currentUser, setCurrentUser] = React.useState({}); // переменная состояния, хранящая данные текущего пользователя
  const [loggedIn, setLoggedIn] = React.useState(false);
  const [userEmail, setUserEmail] = React.useState('');



  const [isTooltipSuccessPopup, setIsTooltipSuccessPopup] = React.useState(false);//+++++

  const navigate = useNavigate();

  React.useEffect(() => {
    if (loggedIn) {
      Promise.all([api.getUserData(), api.getInitialCards()])
        .then(([userData, initialCards]) => {
          setCurrentUser(userData.user);
          setCards(initialCards.cards);
        })
        .catch((err) => console.log(err));
    }
  }, [loggedIn]);

  function handleCardClick(card) {
    setSelectedCard(card);
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }

  function closeAllPopups() {
    setIsEditAvatarPopupOpen(false);
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsTooltipPopupOpen(false);
    setIsTooltipSuccessPopup(false);//+++++
    setSelectedCard({});
  }

  function handleCardLike(card) {
    const isLiked = card.likes.some((i) => i === currentUser._id);

    api
      .changeLikeCardStatus(card._id, isLiked)
      .then((newCard) => {
        setCards((state) =>
          state.map((c) => (c._id === card._id ? newCard.card : c))
        );
      })
      .catch((err) => console.log(err));
  }

  function handleCardDelete(card) {
    api
      .deleteCard(card._id)
      .then(() => setCards((state) => state.filter((c) => c._id !== card._id)))
      .catch((err) => console.log(err));
  }

  //+++++++++++++++++++++++++++++++++++++
  function handleUpdateUser(user) {
    api
      .patchUserData(user)
      .then((userData) => {
        setCurrentUser(userData.user);
        closeAllPopups();
      })
      .catch((err) => {
        console.log(`Ошибка редактирования профиля. ${err}`);
      });
  }

  //+++++++++++++++++++++++++++++++++++++
  function handleUpdateAvatar(avatar) {
    api
      .patchAvatar(avatar)
      .then((userData) => {
        setCurrentUser(userData.user);
        closeAllPopups();
      })
      .catch((err) => console.log(`Ошибка редактирования аватара. ${err}`));
  }

  //+++++++++++++++++++++++++++++++++++++
  function handleAddPlaceSubmit(card) {
    api
      .postCard(card)
      .then((cardData) => {
        setCards([cardData.card, ...cards]);
        closeAllPopups();
      })
      .catch((err) => console.log(`Ошибка добавления карточки. ${err}`));
  }

  function handleLogin(email, password) {
    auth
      .authorizeUser(email, password)
      .then((data) => {
        if (data) {
          setLoggedIn(true);
          setUserEmail(email);
          navigate('/', { replace: true });
        }
      })
      .catch((err) => console.log(err));
  }

  function handleRegister(email, password) {
    auth
      .registerUser(email, password)
      .then((data) => {
        setIsTooltipSuccessPopup(true);
        navigate('/sign-in', { replace: true });
      })
      .catch((err) => {
        setIsSuccess(false);
        console.log(err);
      })
      .finally(() => {
        setIsTooltipPopupOpen(true);
      });
  }

  React.useEffect(() => {
    auth
      .checkToken()
      .then((data) => {
        if (data) {
          setLoggedIn(true);
          // сохранить e-mail для хэдэра
          setUserEmail(data.user.email);
          navigate('/', { replace: true });
        }
      })
      .catch((err) => console.log(err));
  }, []);

  function handleSignOut() {
    auth
      .logoutUser()
      .then(() => {
        setLoggedIn(false);
        setUserEmail('');
        navigate('/sign-in');
      })
      .catch((err) => console.log(err));
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <Header userEmail={userEmail} handleSignOut={handleSignOut} />
      <Routes>
        <Route
          path='/'
          element={
            <ProtectedRoute
              element={Main}
              loggedIn={loggedIn}
              onEditProfile={handleEditProfileClick}
              onAddPlace={handleAddPlaceClick}
              onEditAvatar={handleEditAvatarClick}
              onCardClick={handleCardClick}
              onCardLike={handleCardLike}
              onCardDelete={handleCardDelete}
              cards={cards}
            />
          }
        />
        <Route
          path='/sign-up'
          element={<Register loggedIn={loggedIn} onRegister={handleRegister} />}
        />
        <Route
          path='/sign-in'
          element={<Login loggedIn={loggedIn} onLogin={handleLogin} />}
        />
        <Route path='/*' element={<Navigate to='/sign-in' />} />
      </Routes>
      <Footer />

      {/* ++++++++++++++ */}
      <EditProfilePopup
        isOpen={isEditProfilePopupOpen}
        onClose={closeAllPopups}
        onUpdateUser={handleUpdateUser}
      />
      {/* ++++++++++++++ */}
      <AddPlacePopup
        isOpen={isAddPlacePopupOpen}
        onClose={closeAllPopups}
        onUpdateCards={handleAddPlaceSubmit}
      />
      {/* ++++++++++++++ */}
      <EditAvatarPopup
        isOpen={isEditAvatarPopupOpen}
        onClose={closeAllPopups}
        onUpdateAvatar={handleUpdateAvatar}
      />
      <PopupWithForm title='Вы уверены' name='remove' buttonText='Да'>
        {' '}
      </PopupWithForm>

      {/* ++++++++++++++ */}
      <ImagePopup card={selectedCard} onClose={closeAllPopups} />

      <InfoTooltip
          isOpen={isTooltipSuccessPopup}
          onClose={closeAllPopups}
          title={"Вы успешно зарегистрировались!"}
          image={success}
        />

        <InfoTooltip
          isOpen={isTooltipErrorPopup}
          onClose={closeAllPopups}
          title={"Что-то пошло не так! Попробуйте ещё раз."}
          image={error}
        />
    </CurrentUserContext.Provider>
  );
}

export default App;
