class Api {
  constructor({ baseUrl, headers, credentials }) {
    this._baseUrl = baseUrl;
    this._headers = headers;
    this._credentials = 'include';
  }
  //Проверка ответа
  _checkResponse(res) {
    if (res.ok) {
      return res.json();
    } else {
      return Promise.reject(`Ошибка: ${res.status}`);
    }
  }
  //Обработка данных ответа
  _handleData(url, { method, credentials, headers, body }) {
    return fetch(`${this._baseUrl}${url}`, {
      method,
      headers,
      credentials,
      body,
    }).then(this._checkResponse);
  }
  //Загрузка карточек
  getInitialCards() {
    return this._handleData('/cards', {
      method: 'GET',
      credentials: 'include',
      headers: this._headers,
    });
  }
  //Загрузка данных пользователя
  //   getUserData() {
  getUserInfo() {
    return this._handleData('/users/me', {
      method: 'GET',
      credentials: 'include',
      headers: this._headers,
    });
  }







  sendUserInfo({ name, about }) {
    return this._handleData('/users/me', {
      method: 'PATCH',
      credentials: this._credentials,
      headers: this._headers,
      body: JSON.stringify({ name, about }),
    });
  }

  sendCardInfo({ name, link }) {
    return this._handleData('/cards', {
      method: 'POST',
      credentials: this._credentials,
      headers: this._headers,
      body: JSON.stringify({ name, link }),
    });
  }

  removeCard(cardId) {
    return this._handleData(`/cards/${cardId}`, {
      method: 'DELETE',
      credentials: this._credentials,
      headers: this._headers,
    });
  }

  putLike(cardId) {
    return this._handleData(`/cards/${cardId}/likes`, {
      method: 'PUT',
      credentials: this._credentials,
      headers: this._headers,
    });
  }

  removeLike(cardId) {
    return this._handleData(`/cards/${cardId}/likes`, {
      method: 'DELETE',
      credentials: this._credentials,
      headers: this._headers,
    });
  }

  changeLikeCardStatus(cardId, isLiked) {
    return isLiked ? this.removeLike(cardId) : this.putLike(cardId);
  }

  sendAvatarInfo(avatar) {
    return this._handleData('/users/me/avatar', {
      method: 'PATCH',
      credentials: 'include',
      headers: this._headers,
      body: JSON.stringify(avatar),
    });
  }
}

const api = new Api({
  baseUrl: 'http://localhost:3000',
  credentials: 'include',
  headers: {
    'Content-Type': 'application/json',
  },
});

export { api, Api };
